            $json['boleto']                 = $this->boleto['value'];
        }
        if (!empty($this->manualBilling)) {
            $json['manual_billing']         = $this->manualBilling['value'];
        }

        return (!$asArrayWhenEmpty && empty($json)) ? new stdClass() : $json;
    }
}
